package week1.day1;

public class Basicdatatypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float browserVersion = 130.06F; // float value should have f or F
		String broswerName = "firefox";
		boolean isVisible = true;  //boolean default value always false
		int releaseYear = 1998;
		char browserLogo = 'f';
		
		System.out.println("Browser Version"+ browserVersion);
		System.out.println("Broswer Name:" + broswerName);
		System.out.println("Broswer is Open:" + isVisible);
		System.out.println("Broswer releaser Year:"+releaseYear);
		System.out.println("Broswer logo:"+browserLogo);
		

	}

}
